        // 如果是⼀一个 promise 对象，就会调⽤ then 方法，形成一个嵌套
        if (ret && _isFunction(ret['then'])) {
          ret.then(function (value) {
            resolve(value);
          })
        } else {
          resolve(ret)
        }
      }
      if (_this._status === 'PENDING') {
       _this._resolves.push(handle);
      } else if(promise._status === 'FULFILLED'){
        handle(value);
      }       
    })
  }

